// AIに任せる: 型定義とバリデーション
const userSchema = z.object({
  name: z.string().min(1),
  email: z.string().email(),
  role: z.enum(["admin", "member"]),
});

// 人間が判断: ビジネスルール
function canAccessResource(user: User, resource: Resource): boolean {
  // 権限ロジックは人間が設計・実装する
  return user.role === "admin" || resource.ownerId === user.id;
}
