// 今必要なことだけ
class UserRepository {
  async findAll(): Promise<User[]> {
    return db.query('SELECT * FROM users');
  }

  async findById(id: string): Promise<User | null> {
    return db.query(
      'SELECT * FROM users WHERE id = ?',
      [id]
    );
  }
}

