// Vestigial Code: IE6-8 compatibility (2008)
function addEvent(el, type, fn) {
  if (el.addEventListener) {
    el.addEventListener(type, fn, false);
  } else if (el.attachEvent) { // IE path
    el.attachEvent('on' + type, fn);
  } else {
    el['on' + type] = fn; // fallback
  }
}
